mapper.insert:
INSERT INTO city_file (
	city_id
	, file_name
	, file_ext
	, file_size
) VALUES (
	#{cityId}
	, #{fileName}
	, #{fileExt}
	, #{fileSize}
)

mapper.select:
SELECT city_id AS cityId , city_name AS cityName FROM city
WHERE city_name LIKE CONCAT("%", #{word}, "%")
ORDER BY cityName ASC LIMIT #{startPage}, #{pagePerRow}

mapper.update:
UPDATE city
SET city_name = #{cityName}
WHERE city_id = #{cityId}
